import React, { Component } from 'react';
import web3 from '../Ethereum/web3'
const compiledVoting = require('../Ethereum/build/Election.json')
const HDWalletProvider = require('truffle-hdwallet-provider')

class votingInstance extends Component{

    static getInitialProps = async() => {

        const provider = new HDWalletProvider(
            'shed diamond calm nut coil affair jungle topic hunt wink canyon eight',
            "https://rinkeby.infura.io/v3/138f01e0d4af4a63a27059a502f2bea8"
        )
        const web3 = new Web3(provider);

        const accounts = await web3.eth.getAccounts();

        const voting = await new web3.eth.Contract(JSON.parse(compiledVoting.interface))
        .deploy({data : '0x' + compiledVoting.bytecode})
        .send({from : accounts[0]})

        console.log("Contract is deployed to : ", voting.options.address);
        //const Voting = await voting.methods.owner().call()

        return{
            address : voting.options.address,
            manager : Voting
        }
    }

    render(){
        return(
            <div>
                <h3>Contract is deployed to : {this.props.address}</h3>            
                <h3>Contract is deployed by : {this.props.manager}</h3>
            </div>
        );
    }
}

export default votingInstance;
