import React, { Component } from 'react'
import Layout from '../Components/Layout'
import {Link} from '../routes'
import { Grid, Card, Input, Form, Button, Message } from 'semantic-ui-react'
import voting from '../Ethereum/voting'
import web3 from '../Ethereum/web3'

class votingPage extends Component {

    state = {
        id : '',
        Loading : false,
        errorMessage : ''
    }

    static getInitialProps = async (props) =>{

        const numCandidates = await voting.methods.getNumCandidate().call()

        const candidates = await Promise.all(
            Array(parseInt(numCandidates))
            .fill()
            .map((element,index) =>{
                return voting.methods.candidates(index).call()
            })
        )

        return {
            numCandidates : numCandidates,
            candidates : candidates,
        }
    }

    renderCandidates = () =>{
        const items =  this.props.candidates.map((candidate,index) =>{
            return{
                header : candidate.name,
                meta : index,
                description : candidate.partyName
            }
        });

        return <Card.Group textAlign = "center" centered items = {items} />
    }
    /*
    onSubmit = async(event) =>{
        event.preventDefault();

        const accounts = await web3.eth.getAccounts();

        this.setState({Loading : true, errorMessage : ''})

        try{
            await voting.methods.vote(parseInt(this.state.id)).send({
                from : accounts[0]
            })
        }catch(err){
            this.setState({errorMessage : err.message})
        }
        
        this.setState({Loading : false, id : ''})
    }
    */
    render(){
        return(
            <Layout>
                <Grid>
                    <Grid.Row>
                        <Grid.Column>
                            <Link route ='/'>
                                <a>
                                    Homepage
                                </a>
                            </Link>
                        </Grid.Column>
                    </Grid.Row>
                    <Grid.Row>
                        <Grid.Column>
                            {this.renderCandidates()}
                        </Grid.Column>
                    </Grid.Row>
                    <Grid.Row>
                        <Grid.Column>
                            <Input 
                                value = {this.state.id}
                                onChange = {event => this.setState({id})}
                                fluid
                            />
                        </Grid.Column>
                    </Grid.Row>
                </Grid>
            </Layout>
        )
    }
}

export default votingPage;
