import React, { Component } from 'react'
import Layout from '../Components/Layout'
import {Link} from '../routes'
import { Grid, Card, Form } from 'semantic-ui-react'
import voting from '../Ethereum/voting'

class votingPage extends Component {

    states = {
        index : ''
    }

    static getInitialProps = async (props) =>{

        const numCandidates = await voting.methods.getNumCandidate().call()

        const candidates = await Promise.all(
            Array(parseInt(numCandidates))
            .fill()
            .map((element,index) =>{
                return voting.methods.candidates(index).call()
            })
        )

        return {
            numCandidates : numCandidates,
            candidates : candidates,
        }
    }

    renderCandidates = () =>{
        const items =  this.props.candidates.map((candidate,index) =>{
            return{
                header : candidate.name,
                meta : index,
                description : candidate.partyName
            }
        });

        return <Card.Group textAlign = "center" centered items = {items} />
    }

    render(){
        return(
            <Layout>
                <Grid>
                    <Grid.Row>
                        <Grid.Column>
                            <Link route ='/'>
                                <a>
                                    Homepage
                                </a>
                            </Link>
                        </Grid.Column>
                    </Grid.Row>
                    <Grid.Row>
                        <Grid.Column>
                            {this.renderCandidates()}
                        </Grid.Column>
                    </Grid.Row>
                    <Grid.Row>
                        <Grid.column>
                            <Form>
                                <Form.Feild>
                                    <label>Enter The Voting Index</label>
                                    <Input  
                                        value = {this.state.index}
                                        
                                    />
                                </Form.Feild>
                            </Form>
                        </Grid.column>
                    </Grid.Row>
                </Grid>
            </Layout>
        )
    }
}

export default votingPage
