import React, { Component } from 'react'
import Layout from '../../Components/Layout'
import {Link} from '../../routes'
import {Form, Button, Input} from 'semantic-ui-react'

class newCandidate extends Component{

    state = {
        name : '',
        partyName : '',
        Loading : false
    }

    render(){
        return(
            <Layout>
                <Link route = '/index'>
                    <a>
                        Homepage
                    </a>
                </Link>
                <Form>
                    <Form.Field>
                        <label>Candidate Name</label>
                        <Input 
                            value = {this.state.name}
                            onChange = {event => this.setState({name : event.target.value})}
                        />
                    </Form.Field>
                    <Form.Field>
                        <label>Candidate's Party's Name</label>
                        <Input 
                            value = {this.state.partyName}
                            onChange = {event => this.setState({partyName : event.target.value})}
                        />
                    </Form.Field>
                    <Button primary Loading = {this.state.loadi}>Submit</Button>
                </Form>
                
            </Layout>
        )
    }
}

export default newCandidat
